import './App.css';
import React, {useState,useEffect } from 'react';
import Navbar from './components/Navbar';
import {} from "./components/SearchContext"

const getStock = (symbol, setStockData) => {
  //how to stop firing on intial render 
  //https://stackoverflow.com/questions/72146986/useeffect-firing-on-initial-render
  symbol && fetch(`http://127.0.0.1:5000/v0/ticker?id=` + symbol, {
  method: 'GET',
  headers: new Headers({
    'Content-Type': 'application/x-www-form-urlencoded',
  }),
})
  .then((response) => {
    if(!response.ok) {
      throw response;
    }
    return response.json();
  })
  .then((json) => {
    setStockData(json)
  })
}
function App() {
  const handleSubmit = (event) => {
    event.preventDefault();
    setSymbol(stock);
  }
  // react states
  const [symbol, setSymbol] = useState();
  const [stock, setStock] = useState();
  const [stockData, setStockData] = useState([]);

  const stonks = stockData[0];
  let price = undefined;
  let change = undefined; 
  let ticker = undefined;
  let dailyChange = undefined;
  useEffect(() => {
    getStock(symbol, setStockData);
  }, [symbol]);
  if(stonks !== undefined) {
    price = stonks.price;
    change = stonks.change;
    ticker = stonks.symbol;
    dailyChange = stonks.dailyChange;
  }
  return (
    <div>
    <Navbar/>
    <div margin-left="4px">
      <form onSubmit = {handleSubmit}>
        <input 
        type = "text" 
        name = "name"
        onChange= {(event) =>
          setStock(event.target.value)}
        />
        <button type = "submit"> Submit</button>
      </form>

      <table>
        <tr>
          <th>Ticker Name</th>
          <th>Change</th>
          <th>Daily Change</th>
          <th>Current Price</th>
        </tr>
        <tr>
          <td>{ticker}</td>
          <td>{change}</td>
          <td>{dailyChange}</td>
          <td>{price}</td>
        </tr>
      </table>
      <img src="./logo.svg" alt="" width="40" height="40"/>
      <ol type = "1">
        
      </ol>
    </div>
    </div>
  );
}

export default App;
