import './App.css';
import React, {useState,useEffect } from 'react';
const getStock = (symbol) => {
  //how to stop firing on intial render 
  //https://stackoverflow.com/questions/72146986/useeffect-firing-on-initial-render
  symbol && fetch(`http://127.0.0.1:5000/v0/ticker?id=` + symbol, {
  method: 'GET',
  headers: new Headers({
    'Content-Type': 'application/x-www-form-urlencoded',
  }),
})
  .then((response) => {
    if(!response.ok) {
      throw response;
    }
    return response.json();
  })
  .then((json) => {
    setStockData(json)
  })
}
function App() {
  // const [stock, setStock] = useState([]);

  const handleSubmit = (event) => {
    event.preventDefault();
    setSymbol(stock);
  }
  const [symbol, setSymbol] = useState();
  const [stock, setStock] = useState();
  const [stockData, setStockData] = useState();
  useEffect(() => {
    getStock(symbol, setStockData)
  }, [symbol]);
  return (
    <div>
    <form onSubmit = {handleSubmit}>
    <input 
    type = "text" 
    name = "name"
    onChange= {(event) =>
      setStock(event.target.value)}
    />
    <button type = "submit"> Submit</button>
    </form>
    {stockData}
    </div>
  );
}

export default App;
